import React,{Component} from "react"
import "./style.css"
// import style from "./style.module.css"
import axios from "axios"

export default class First extends Component {
    state={
        weather:[]
    }
    getData=()=>{
        axios.get("https://restapi.amap.com/v3/weather/weatherInfo",{
            params:{
                key:"b51d40625115ffd635190dfea1f6167e",
                city:"昆明"
            }
        }).then((res)=>{
            const {lives}=res.data
            
            this.setState({weather:lives[0]})    
        })
    }

    componentDidMount(){
        this.getData()
    }

  render() {
    const {province,city,temperature}=this.state.weather
    return (
      <div>
        <h1 className="title">这是First组件</h1>
        {/* <h2 className={style.module}>模块化的样式</h2> */}
        <h1>{province}{city}的温度是{temperature}</h1>
        
      </div>
    )
  }
}
