import React from "react";
import axios from "axios"


class Hello extends React.Component{
    constructor(){
        this.state={
            city:"",
            tem:0
        }
    }
    getData=()=>{
        axios.get("https://restapi.amap.com/v3/weather/weatherInfo",{
            params:{
                key:"b51d40625115ffd635190dfea1f6167e",
                city:"成都"
            }
        }).then((res)=>{console.log(res);this.setState({city:res.data.lives.city,tem:res.data.lives.temperature})})
    }
    render(){
        return (<div><h1>你好啊</h1>
        <button onClick={this.getData}></button>
        <h2>{this.state.city}的温度是 {this.state.temperature}</h2>
        </div>)
    }
}


export default Hello
